import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import './CaseGallery.css';

const CaseGallery = ({ title, images, descriptions, captions, conclusion, links }) => {
  const [showModal, setShowModal] = useState(false);
  const [activeIndex, setActiveIndex] = useState(0);

  const openModal = (index) => {
    setActiveIndex(index);
    setShowModal(true);
  };

  const closeModal = () => setShowModal(false);

  return (
    <div className="container my-5">
      <div className="custom-card">
        <h2 className="mb-4">{title}</h2>

        <div className="row">
          {images?.map((image, index) => (
            <div key={index} className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
              <img
                src={image.src}
                alt={`Case study image ${index + 1}`}
                className="img-fluid rounded shadow-sm"
                style={{ cursor: 'pointer' }}
                onClick={() => openModal(index)}
              />
              <p className="mt-2 text-center text-muted small">{captions?.[index]}</p>
            </div>
          ))}
        </div>

        <Modal show={showModal} onHide={closeModal} centered size="lg">
          <Modal.Header closeButton>
            <Modal.Title>{captions?.[activeIndex]}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <img
              src={images?.[activeIndex]?.src}
              alt="Expanded case study"
              className="img-fluid w-100 mb-3"
            />
            <p>{descriptions?.[activeIndex]}</p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={closeModal}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>

        {conclusion && <p className="mt-4"><strong>Conclusion:</strong> {conclusion}</p>}

        {links?.length > 0 && (
          <div className="mt-3">
            <strong>Links:</strong>
            <ul>
              {links.map((link, index) => (
                <li key={index}>
                  <a href={link.url} target="_blank" rel="noopener noreferrer">
                    {link.text}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default CaseGallery;
