import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CaseGallery = () => {
  const [selectedIndex, setSelectedIndex] = useState(null);

  const items = [
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.1.png",
      description: "Interviews with client, user research, and competitor analysis informed the design process... etc.",
    },
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.1.png",
      description: "Interviews with client, user research, and competitor analysis informed the design process... etc.",
    },
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.1.png",
      description: "Interviews with client, user research, and competitor analysis informed the design process... etc.",
    }

  ];

  return (
    <div className="container">
      <div className="row">
        {items.map((item, index) => (
          <div
            className="col-md-4 mb-4"
            key={index}
            onClick={() => setSelectedIndex(index)}
            style={{ cursor: 'pointer' }}
          >
            <div className="position-relative">
              <img src={item.image} className="img-fluid" alt="Case" />
              <div className="overlay-text position-absolute bottom-0 start-0 p-2 bg-dark bg-opacity-50 text-white w-100">
                {item.title}
              </div>
            </div>
          </div>
        ))}
      </div>

      <Modal
        show={selectedIndex !== null}
        onHide={() => setSelectedIndex(null)}
        size="xl"
        centered
        dialogClassName="full-modal"
        contentClassName="full-modal-content"
        scrollable
      >
        <Modal.Header closeButton>
          <Modal.Title>{items[selectedIndex]?.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <img src={items[selectedIndex]?.image} alt="" className="img-fluid mb-3" />
          <p>{items[selectedIndex]?.description}</p>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default CaseGallery;
