import React, { useState } from 'react';
import { Modal, Button, Card } from 'react-bootstrap';
import './CaseStudyGallery.css';
import { useNavigate } from 'react-router-dom';

const CaseStudyGallery = ({ items }) => {
  const [showModalIndex, setShowModalIndex] = useState(null);
  const navigate = useNavigate();

  const handleOpenModal = (index) => setShowModalIndex(index);
  const handleCloseModal = () => setShowModalIndex(null);

  return (
    <div className="container my-5">
      <div className="row">
        {items.map((item, index) => (
          <div
            key={index}
            className="col-12 col-md-4 mb-4 position-relative gallery-item"
            onClick={() => handleOpenModal(index)}
          >
            <img src={item.image} alt={item.title} className="img-fluid rounded shadow" />
            <div className="overlay-text">
              <h5>{item.title}</h5>
              <p>{item.subtitle}</p>
            </div>

            {/* Modal for each item */}
            <Modal show={showModalIndex === index} onHide={handleCloseModal} centered>
              <Card className="p-3">
                <Card.Body>
                  <Card.Title>{item.title}</Card.Title>
                  <Card.Text>{item.preview}</Card.Text>
                  <Button variant="primary" onClick={() => navigate(item.link)}>
                    View Full Case Study
                  </Button>
                </Card.Body>
              </Card>
            </Modal>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CaseStudyGallery;
