import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaEnvelope } from 'react-icons/fa';

const Navbar = () => {
  const [showMobileMenu, setShowMobileMenu] = useState(false);

  const handleMobileMenuClick = () => {
    setShowMobileMenu(!showMobileMenu);
  };

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '1rem', position: 'sticky', top: '0', backgroundColor: '#333', zIndex: '1000' }}>
      {/* logo*/}
      <div>
        <Link to="/">
          <img src="/images/logo-white.png" alt="Prizmpix Logo" style={{ height: '40px' }} />
        </Link>
      </div>

      {/* Navlinks*/}
      <ul style={{ listStyle: 'none', display: 'flex', justifyContent: 'space-between', alignItems: 'center', margin: '0 auto', marginBottom: '0.5rem' }}>
        <li style={{ marginRight: '20px' }}><Link to="/">Home</Link></li>
        {/* <li style={{ marginRight: '20px' }}><Link to="/services">Services</Link></li> */}
        <li style={{ marginRight: '20px' }}><Link to="/about">About</Link></li>
        <li style={{ marginRight: '20px' }}><Link to="/collaboration">Collaboration</Link></li>
        <li style={{ marginRight: '20px' }}><Link to="/portfolio">Portfolio</Link></li>
      </ul>

      {/* Icons*/}
      <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginLeft: '20px' }}>
        <Link to="/contact" style={{ marginRight: '20px' }}><FaEnvelope size={24} /></Link>
      </div>

      {/* Mobile menu button */}
      <div className="mobile-menu-button" onClick={handleMobileMenuClick} style={{ cursor: 'pointer', marginLeft: '20px' }}>
        <FaBars size={24} />
      </div>

      {/* Mobile menu */}
      <div className={`mobile-menu ${showMobileMenu ? 'show' : ''}`} style={{ position: 'absolute', top: '100%', left: '0', width: '100%', backgroundColor: '#333', padding: '10px' }}>
        <ul style={{ listStyle: 'none', padding: '0', margin: '0' }}>
          <li style={{ marginBottom: '10px' }}><Link to="/">Home</Link></li>
          {/* <li style={{ marginBottom: '10px' }}><Link to="/services">Services</Link></li> */}
          <li style={{ marginBottom: '10px' }}><Link to="/about">About</Link></li>
          <li style={{ marginBottom: '10px' }}><Link to="/collaboration">Collaboration</Link></li>
          <li style={{ marginBottom: '10px' }}><Link to="/portfolio">Portfolio</Link></li>
          <li style={{ marginBottom: '10px' }}><Link to="/contact">Contact</Link></li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
