import React, { useState } from 'react';
import './CardGrid.css';

const CardGrid = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedDescription, setSelectedDescription] = useState(null);

  const handleImageClick = (src, description) => {
    setSelectedImage(src);
    setSelectedDescription(description);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
    setSelectedDescription(null);
  };

  const images = [
    { src: "/images/spacing-1.png", alt: "Image 1", description: "The project began with stakeholder interviews, user research, and competitor analysis to understand the client's goals, target audience, and market trends. Key findings included the need for a modern design, improved navigation, and enhanced mobile experience." },
    { src: "/images/spacing-1.png", alt: "Image 2", description: "Based on research, user personas were created to guide the design process. Low-fidelity wireframes were developed to visualize the site's structure and layout. Framer's design tools were used to create interactive wireframes, testing navigation and user flows." },
    { src: "/images/spacing-1.png", alt: "Image 3", description: "High-fidelity designs were created using Framer, incorporating the client's brand guidelines and design preferences. The design system included a custom color palette, typography, and component library. Responsive design principles were applied to ensure a seamless experience across devices." },
    { src: "/images/spacing-1.png", alt: "Image 4", description: "Interactive prototypes were built using Framer, allowing stakeholders and users to test the site's functionality and usability. Feedback was gathered and iterated upon, refining the design and ensuring a smooth user experience." },
  ];

  const descriptions = [
    { text: "I worked on development, where the site was built using Framer's development tools. The site was optimized for performance, accessibility, responsiveness and SEO. We did bug testing enrolling helpers to do testing from various devices to ensure a consistent experience across devices." },
    { text: "The site was launched, and analytics were set up to track user behavior and performance. Post-launch evaluation revealed a significant improvement in user engagement, mobile traffic, and conversion rates. The redesign achieved the client's goals, providing a modern, user-friendly experience that aligned with their brand and objectives." },
    { text: "This case study demonstrates the UX design and development process using Framer, highlighting the importance of research, user-centered design, and iterative testing in creating a successful responsive website redesign." },
    { text: "Progress on updating the https://jolly-evolution-401688.framer.app" },
  ];

  return (
    <div className="card-grid">
      <h2>Case Study 1</h2>
      <div className="image-grid">
        <div className="image-row">
          {images.slice(0, 2).map((image, index) => (
            <div className="image-box" key={index}>
              <img src={image.src} alt={image.alt} onClick={() => handleImageClick(image.src, image.description)} />
            </div>
          ))}
        </div>
        <div className="description-row">
          {images.slice(0, 2).map((image, index) => (
            <div className="description-box" key={index}>
              {image.description}
            </div>
          ))}
        </div>
        <div className="image-row">
          {images.slice(2, 4).map((image, index) => (
            <div className="image-box" key={index}>
              <img src={image.src} alt={image.alt} onClick={() => handleImageClick(image.src, image.description)} />
            </div>
          ))}
        </div>
        <div className="description-row">
          {images.slice(2, 4).map((image, index) => (
            <div className="description-box" key={index}>
              {image.description}
            </div>
          ))}
        </div>
      </div>
      <p>
        https://special-channel-275950.framer.app 
      </p>
      <div className={`modal-overlay ${selectedImage ? 'show' : ''}`} onClick={handleCloseModal}>
        <div className="modal-content">
          {selectedImage && (
            <>
              <img src={selectedImage} alt="Selected Image" />
              <div className="modal-description">{selectedDescription}</div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default CardGrid;
