import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CaseGallery = () => {
  const [selectedIndex, setSelectedIndex] = useState(null);

  const items = [
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.1.png",
      description: "Interviews with client, user research, and competitor analysis informed the design process... etc.",
    },
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.1.png",
      description: "Interviews with client, user research, and competitor analysis informed the design process... etc.",
    },
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.1.png",
      description: "Interviews with client, user research, and competitor analysis informed the design process... etc.",
    }

  ];

  return (
    <div className="container">
      <div className="row">
        {items.map((item, index) => (
          <div
            className="col-md-4 mb-4"
            key={index}
            onClick={() => setSelectedIndex(index)}
            style={{ cursor: 'pointer' }}
          >
            <div className="position-relative">
              <img src={item.image} className="img-fluid" alt="Case" />
              <div className="overlay-text position-absolute bottom-0 start-0 p-2 bg-dark bg-opacity-50 text-white w-100">
                {item.title}
              </div>
            </div>
          </div>
        ))}
      </div>

      <Modal
  show={selectedIndex !== null}
  onHide={() => setSelectedIndex(null)}
  backdrop={true}
  size="xl"
  centered
  scrollable
>
  <div className="modal-content frosted">
    <Modal.Header closeButton closeVariant="white">
      <Modal.Title>{images[selectedIndex]?.caption}</Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <img
        src={images[selectedIndex]?.src}
        alt={`Image ${selectedIndex + 1}`}
        className="img-fluid mb-3"
      />
      <h5>{images[selectedIndex]?.caption}</h5>
      <p>{images[selectedIndex]?.description}</p>
    </Modal.Body>
  </div>
</Modal>
    </div>
  );
};

export default CaseGallery;
