import React, { useState } from 'react';
import { Modal, Button, Card } from 'react-bootstrap';
import './CaseGallery.css';
import { useHistory } from 'react-router-dom';

const CaseGallery = ({ items }) => {
  const [showModalIndex, setShowModalIndex] = useState(null);
  const history = useHistory();

  const handleOpenModal = (index) => setShowModalIndex(index);
  const handleCloseModal = () => setShowModalIndex(null);

  return (
    <div className="container my-5">
      <div className="row">
        {items.map((item, index) => (
          <div
            key={index}
            className="col-12 col-md-4 mb-4 position-relative gallery-item"
            onClick={() => handleOpenModal(index)}
          >
            <img src={item.image} alt={item.title} className="img-fluid rounded shadow" />
            <div className="overlay-text">
              <h5>{item.title}</h5>
              <p>{item.subtitle}</p>
            </div>

            {/* Modal for each item */}
                <Modal
                    show={selectedIndex !== null}
                    onHide={() => setSelectedIndex(null)}
                    size="xl"
                    centered
                    dialogClassName="full-modal"
                    contentClassName="full-modal-content"
                    scrollable
                >
                <Modal.Header closeButton>
                    <Modal.Title>{items[selectedIndex]?.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="case-detail-body">
                    <img
                        src={items[selectedIndex]?.image}
                        alt="Expanded"
                        className="img-fluid mb-3"
                    />
                    <p>{items[selectedIndex]?.description}</p>
                    {/* Add more scrollable content here */}
                    </div>
                </Modal.Body>
                </Modal>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CaseGallery;
