import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './CaseGallery.css'; // make sure to create this CSS file for frosted styles

const CaseGallery = () => {
  const [selectedIndex, setSelectedIndex] = useState(null);

  const items = [
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.1.png",
      description: "Interviews with client, user research, and competitor analysis informed the design process... etc.",
    },
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.2.jpeg",
      description: "Low-fidelity wireframes were developed and refined into high-fidelity designs using Framer... etc.",
    },
    {
      title: "Case Study 1: Equality Fashion Week Rebuild",
      image: "/images/case1.3.png",
      description: "Interactive prototypes were built and tested with users on different devices... etc.",
    }
  ];

  return (
    <div className="container my-5">
      <div className="row">
        {items.map((item, index) => (
          <div
            className="col-12 col-md-4 mb-4"
            key={index}
            onClick={() => setSelectedIndex(index)}
            style={{ cursor: 'pointer' }}
          >
            <div className="position-relative">
              <img src={item.image} className="img-fluid w-100" alt={`Case ${index + 1}`} />
              <div className="overlay-text position-absolute bottom-0 start-0 p-2 text-white w-100">
                <div className="bg-dark bg-opacity-50 p-2">{item.title}</div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <Modal
        show={selectedIndex !== null}
        onHide={() => setSelectedIndex(null)}
        centered
        size="lg"
        backdropClassName="modal-backdrop-frosted"
      >
        <div className="modal-content frosted">
          <Modal.Header closeButton closeVariant="white">
            <Modal.Title>{items[selectedIndex]?.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body style={{ maxHeight: '70vh', overflowY: 'auto' }}>
            <img
              src={items[selectedIndex]?.image}
              alt={`Case Image ${selectedIndex + 1}`}
              className="img-fluid mb-3"
            />
            <p>{items[selectedIndex]?.description}</p>
          </Modal.Body>
        </div>
      </Modal>
    </div>
  );
};

export default CaseGallery;
