import React, { useState } from 'react';

const CaseStudyGallery = ({
  title,
  images = [],
  descriptions = [],
  captions = [],
  conclusion,
  links = [],
}) => {
  const [selectedIndex, setSelectedIndex] = useState(null);

  const handleImageClick = (index) => {
    setSelectedIndex(index);
  };

  const handleCloseModal = () => {
    setSelectedIndex(null);
  };

  return (
    <div className="container my-5">
      <h2 className="mb-4">{title}</h2>

      <div className="row">
        {images.map((img, index) => (
          <div key={index} className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
            <div className="card h-100 shadow-sm">
              <img
                src={img.src}
                alt={captions[index] || `Image ${index + 1}`}
                className="card-img-top"
                role="button"
                onClick={() => handleImageClick(index)}
              />
              <div className="card-body p-2">
                <p className="card-text small text-center">{captions[index]}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {selectedIndex !== null && (
        <div
          className="modal fade show"
          style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.8)' }}
          onClick={handleCloseModal}
        >
          <div className="modal-dialog modal-lg modal-dialog-centered" onClick={(e) => e.stopPropagation()}>
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">{captions[selectedIndex]}</h5>
                <button type="button" className="btn-close" onClick={handleCloseModal}></button>
              </div>
              <div className="modal-body">
                <img
                  src={images[selectedIndex].src}
                  alt={captions[selectedIndex]}
                  className="img-fluid mb-3"
                />
                <p>{descriptions[selectedIndex]}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {conclusion && (
        <div className="mt-4">
          <h5>Conclusion</h5>
          <p>{conclusion}</p>
        </div>
      )}

      {links?.length > 0 && (
        <div className="mt-3">
          <h6>Related Links:</h6>
          <ul>
            {links.map((link, idx) => (
              <li key={idx}>
                <a href={link.url} target="_blank" rel="noopener noreferrer">
                  {link.text}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CaseStudyGallery;
