import React, { useState } from 'react';
import './CardGrid.css';

const CaseStudy = ({ title, images, descriptions, captions, conclusion, links }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedCaption, setSelectedCaption] = useState(null);

  const handleImageClick = (src, caption) => {
    setSelectedImage(src);
    setSelectedCaption(caption);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
    setSelectedCaption(null);
  };

  return (
    <div>
      <h2>{title}</h2>
      <div className="image-grid">
        {images.map((image, index) => (
          <div key={index} className="card-pair">
            <div className="image-box">
              <img
                src={image.src}
                alt={`Image ${index + 1}`}
                onClick={() => handleImageClick(image.src, captions[index])}
              />
            </div>
            <div className="description-box">
              {descriptions[index]}
            </div>
          </div>
        ))}
      </div>

      <div className="conclusion-container">
        <p>{conclusion}</p>
        <div>
          {links && links.map((link, index) => (
            <p key={index}>
              <a href={link.url} target="_blank" rel="noopener noreferrer">{link.text}</a>
            </p>
          ))}
        </div>
      </div>

      <div className={`modal-overlay ${selectedImage ? 'show' : ''}`} onClick={handleCloseModal}>
        <div className="modal-content">
          {selectedImage && (
            <>
              <img src={selectedImage} alt="Selected" />
              <div className="modal-caption">{selectedCaption}</div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

// Now define CardGrid below
const CardGrid = () => {
